INSERT INTO `#__content` VALUES ('26', 'Автомобильные перевозки по России', 'russiaavto', '', '<p align=\"justify\">Перевозка грузов по России автотранспортом пользуется наибольшей популярностью, как самый удобный  вид доставки товара. Автотранспортное средство подается на место погрузки товара, на склад отправителя груза и доставляется непосредственно получателю, к месту разгрузки. Все наши водители – экспедиторы имеют обширный опыт автомобильных грузовых перевозок по России. Все автотранспортные перевозки по России осуществляются на высоконадежных автотранспортных средствах таких марок как Камаз, Маз, Iveco (Ивеко), Scania (Скания), Renault (Рено), Volvo (Вольво), Man (Ман). В распоряжении компании находятся автотранспортные средства с различной грузоподъемностью и объемом всех вышеперечисленных марок (автопоезда, еврофуры, тентованные фургоны, рефрижераторы, для перевозки грузов в специальном  температурном режиме, например, рефрижераторная перевозка мяса по России; изотермические фургоны, контейнеровозы, для авто перевозки контейнеров по России и т.п.) для автомобильных перевозок грузов по России всех видов грузов. Тарифы на российские автомобильные перевозки зависят от характера, веса и объема перевозимого груза, расстояния между пунктом отправки и пунктом назначения, и др. факторов, поэтому ставка на автоперевозку по России рассчитывается индивидуально для каждого вида перевозимого груза.</p>\r\n<p> </p>\r\n<p> </p>\r\n<div>\r\n<p><strong> </strong></p>\r\n<h1><strong><br /> Для информации по тарифам звоните ***<br /> или заполните нижеприведенную<br /> ЗАЯВКУ НА ПЕРЕВОЗКУ </strong></h1>\r\n<strong> </strong></div>\r\n<p>{mtpform}</p>', '', '1', '0', '0', '0', '2012-03-09 13:31:49', '62', '', '0000-00-00 00:00:00', '0', '0', '0000-00-00 00:00:00', '2012-03-09 13:31:49', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_da